$(document).ready(function(){$(".updateForm button").each(function(){$(this).click(function(e){e.preventDefault();var t=$(this).attr("data-form");$.post("scripts/update_user.script.php",$("#form_"+t).serialize(),function(e){1==e.success?(alert("User successfully updated"),location.reload()):alert(e.message)},"json")})}),$("#createUser").click(function(e){e.preventDefault(),$.post("scripts/create_user.script.php",$("#createForm").serialize(),function(e){1==e.success?location.reload():alert(e.message)},"json")}),$(".updateForm .checkbox input[type=checkbox]").each(function(){$(this).click(function(){var e=$(this).attr("data-toggle");$("#"+e).toggle()})}),$("#showForm").click(function(){$("#newUserForm").slideToggle()})});