$(document).ready(function(){$(".updateForm button").each(function(){$(this).click(function(t){t.preventDefault();var i=$(this).attr("data-form");$.post("scripts/update_user.script.php",$("#form_"+i).serialize(),function(t){1==t.success?(alert("User successfully updated"),location.reload()):alert(t.message)},"json")})}),$("#createUser").click(function(t){t.preventDefault(),$.post("scripts/create_user.script.php",$("#createForm").serialize(),function(t){1==t.success?location.reload():alert(t.message)},"json")}),$(".updateForm .checkbox input[type=checkbox]").each(function(){$(this).click(function(){var t=$(this).attr("data-toggle");$("#"+t).toggle()})}),$("#showForm").click(function(){$("#newUserForm").slideToggle()})});